RegisterServerEvent("chatMessage")AddEventHandler('chatMessage',function(a,b,c)pm=stringsplit(c," ")if pm[1]=="/ad"then CancelEvent()if pm[2]==nil then TriggerClientEvent("chatMessage",a,"",{0,0,0},"^1Usage: ^7/ad [company] ^3 Companies: fleeca, maze, ammunation, lscustoms, pegasus, taxi, traffic")elseif pm[2]~=nil then TriggerClientEvent("mdh:ad:composeAd",a,pm[2])end elseif pm[1]=="/toggleads"then TriggerClientEvent("mdh:ad:toggle",a)RconPrint("Toggle Ads")end end)RegisterServerEvent("mdh:ad:pushAd")AddEventHandler("mdh:ad:pushAd",function(d,c)TriggerClientEvent("mdh:ad",-1,d,c)end)function stringsplit(e,f)if f==nil then f="%s"end;local g={}i=1;for h in string.gmatch(e,"([^"..f.."]+)")do g[i]=h;i=i+1 end;return g end;function tablelength(j)local k=0;for l in pairs(j)do k=k+1 end;return k end